mapper.insert:
insert into _eppdev_table(
id, table_name, table_comment, module_name, entity_name, table_type, version_id, create_sql, origin_table_id, remarks, create_date, create_by, update_date, update_by, del_flag,
) values(
#{id}, #{tableName}, #{tableComment}, #{moduleName}, #{entityName}, #{tableType}, #{versionId}, #{createSql}, #{originTableId}, #{remarks}, #{createDate}, #{createBy}, #{updateDate}, #{updateBy}, #{delFlag},
)

mapper.delete:
update _eppdev_table a

    a.del_flag=1,
    a.update_date=#{updateDate}, 
    a.update_by = #{updateBy}, 


    and a.id=#{id}
    and a.version_id=#{versionId}
    and a.del_flag=#{delFlag}
        and a.del_flag = 0

mapper.update:
update _eppdev_table a
a.id = #{id}, a.table_name = #{tableName}, a.table_comment = #{tableComment}, a.module_name = #{moduleName}, a.entity_name = #{entityName}, a.table_type = #{tableType}, a.version_id = #{versionId}, a.create_sql = #{createSql}, a.origin_table_id = #{originTableId}, a.remarks = #{remarks}, a.create_date = #{createDate}, a.create_by = #{createBy}, a.update_date = #{updateDate}, a.update_by = #{updateBy}, a.del_flag = #{delFlag},
and a.id=#{id}

mapper.select:
select




from _eppdev_table a


    and a.id=#{id}
    and a.table_name=#{tableName}
    and a.version_id=#{versionId}
    and a.del_flag=#{delFlag}
        and a.del_flag = 0
    ${_customWhere}



        order by ${_orderBy}


        order by a.update_date desc
